import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId, isAdmin, Delete } from '../func';
import { useState } from 'react';

function ArticleLists({user, isLogin}){
    const [visible, setVisible] = useState(false);
    const articleList = Array.from(FetchWithoutId("board").data);
    if(!articleList){ return <div> Loading... </div> }
    else {
        return (
            <> <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
            {isLogin && <Link to={`/board/add/0`} id="none"> <button id="btn-post"> Write article </button></Link>}
            {isAdmin(user.auth) && 
                <button id="btn-remove"
                        onClick={() => { setVisible(!visible) }}>  {!visible ? "Select Delete" : "Cancel Delete"} </button>}
            {visible && <div className='box1' style={{top: "167px"}}><SelectDelete user={user} articleList={articleList}/></div>}
            <div className='box2' style={{top: "100px", width: "90%"}}><ArticleList user={user} articleList={articleList} /></div>
            
            <hr/>
            {/* 페이징 처리 추가 */}
            </>
        )
    }
}

function SelectDelete({user, articleList}) {
    let checked = document.getElementsByName("deleteArticle");
    console.log(checked).values();
    return (
        <>
            {isAdmin(user?.auth) && 
                (articleList?.map((article, index) => {
                    return (
                    <>
                    <input type={'checkbox'} key={index} name={"deleteArticle"} style={{width: "20px", height: "20px", margin: "11px"}}/><br/>
                    </>)
                }))
            }
        </>
    )
}

export default ArticleLists;
