import { Link } from "react-router-dom";
import axios from "axios";
import 'w3-css';
import { FetchWithoutId, isAdmin } from "./func";

function Bar({isLogin, removeCookie, user}) {
    const categoryList = Array.from(FetchWithoutId("category").data);
    function Logout() {			
        removeCookie('isLogin');
        axios.post('/logout');
        window.location.replace('/');
    };
    return (
        <><div className="w3-top w3-bar w3-xlarge">
            <Link to={'/'} className="w3-bar-item w3-button">Home</Link>
            <Link to={'/mypage'} className="w3-bar-item w3-button w3-hover-blue">Login</Link>
            <Link to={'/mypage'} className="w3-bar-item w3-button w3-hover-blue">MyPage</Link>
            <button className="w3-bar-item w3-button">Logout</button>
            <div class="w3-dropdown-hover">
                <button class="w3-button">Dropdown</button>
                <div class="w3-dropdown-content w3-card-4">
                    {categoryList?.map((category, index) => (
                        <Link to={`/board/category/${category.id}`} key={index}
                              className="w3-bar-item w3-button"> {category.name}</Link>
                    ))}
                </div>
            </div>
            {isAdmin(user?.auth) &&
                <Link to={"/category"} className="w3-bar-item w3-button w3-hover-red">Setting</Link>
            }
        </div>
        <br/><br/><br/><br/><br/></>

        // <header>
        //     <div style={{height: "80px", textAlign: "center", backgroundColor: "#aa9dff", position: "relative"}}>
        //         <b style={{fontSize: "40px", verticalAlign: "middle"}}> Board </b> 
        //         <Link id="none" to={`/login`}>
        //             <button id="btn-user" style={isLogin ? {display : "none"} : {right: "10px"}}> Login </button>
        //         </Link>
        //         <button style={isLogin ? {right: "10px"} : {display : "none"}} id="btn-user"
        //                 onClick={() => Logout()} > Logout </button>
        //         <Link id="none" to={`/mypage`}>
        //             <button style={isLogin ? {right: "120px"} : {display : "none"}} id="btn-user">My Page</button>
        //         </Link>
        //     </div>
        // </header>
    );
}

export default Bar;
