import React, {useState, useEffect} from "react";

function ArticleDeatil({articleId}){
    const [articleDetail, setArticleDetail] = useState({
        data : {}
    });

    useEffect(()=> {
        const RES = fetch(`/board/${articleId}`)
                        .then((res) => res.json());

        console.log(RES);
        return setArticleDetail({articleDetail:RES});
    }, [articleId, articleDetail])

    return (
        <div>
            {articleListArr.map((article, index) => (
                <Article data={article} index={index} />
            ))}
        </div>
    )
}


function ArticleDetailData({data, index}) {

    return (
        <li id="liNone"> Article Detail : 
            <b> ID : </b> <span> {articleDetail.data.id} </span> <br/>
            <b> Title : </b> <span> </span> <br/>
            <b> Category : </b> <span> </span> <br/>
            <b> Content : </b> <span> </span> <br/>
            <b> Created By : </b> <span> </span> <br/>
            <b> Created At : </b> <span> </span> <br/>
            <b> Visit : </b> <span> </span> <br/>
            <li id="liNone"><b> Comment : </b> <span> 
                
                </span> <br/></li>
            <nav>
                <Link to={`/`}>
                    <button id="btn-detail"> Home </button></Link>
            </nav>
        </li>
    );
}


export default ArticleDeatil;
