import {Link} from 'react-router-dom';
import * as Function from "../func";
import '../App.css'
import _ from 'lodash';

function CategoryBar(){
    const categoryList = Function.Fetching("category", 1);
    const user = Function.Fetching('user', sessionStorage.getItem('username'));

    function isAdmin(){
        if(_.isEqual(user.auth, "ROLE_ADMIN")){
            console.log(user.auth);
            return true;
        }else {
            console.log(_.isEqual(user.auth, "ROLE_ADMIN"));
            return false;
        }
    }
   
    return (
        <div style={{margin: "10px", textAlign: "center", borderBottom: "1px solid #373737", whiteSpace: "nowrap"}}>
            <b style={{color: "#373737", fontSize: "30px", verticalAlign: "middle"}}> Category : </b> 
            {categoryList?.map((category, index) => (
                <Link to={`/board/category/${category.id}`} id="none" key={index}> 
                    <button id="btn-category"> {category.name} </button>
                </Link>
            ))}
            <Link id='none' to={"/category"}>
                <button id='btn-remove' style={isAdmin() ? {display : 'none'} : {display : 'inline-block'}}> Setting </button></Link>
            <br/><br/>
        </div>
    )
}

export default CategoryBar;
