import { useState, useEffect } from "react";
import axios from "axios";
import _ from "lodash";
import { Link } from "react-router-dom";

export function isLogin(){
    if(sessionStorage.getItem('username')){ return true; }
    else { return false; }
 }

export function User(){
    if(isLogin()){
        const user = FetchWithoutId("user").data;
        return user;
    }else{
        return null;
    }
}

export function authority(user){
    return user.authorities[0].authority;
}

export function isAdmin(auth){
    if(_.isEqual(auth, "ROLE_ADMIN")){
        return true;
    }else {
        return false;
    }
}

export function thisUser(createdNickname, user){
    return isLogin() && _.isEqual(createdNickname, user?.nick_name);
}


export function getUrlId(n){
    const urlList = ((window.location.href).split('/'));
    const id = urlList[(urlList.length)-n];
    return id;
}

export function ifError(e){
    if(e.response.status === 400){
        alert("Bad Request");
        window.location.reload();
    }else if(e.response.status === 401){
        alert("Don't have permission.\nPlease Login.");
        window.location.replace("/login");
    }else if(e.response.status === 403){
        alert("Response rejected.");
        window.location.replace("/home");
    }else if(e.response.status === 404){
        <div>
            <h1> SORRY <br/> Page Not Found </h1><hr/>
            <b>We can't found this page.</b>
            <Link id="none" to={`/home`}><button id="btn-remove">HOME</button></Link>
        </div>
    }else{
        alert("Error : " + e.response.status + " " + e.response.statusText + "\nReturn to Board.");
        window.location.replace("/board");
    }
}

export async function FetchWithoutId(dataName){
    // const [data, setData] = useState({ data : {} });
    const data 
        = await axios.get(`/${dataName}`).then((res) => {
            console.log(res?.data);
            setData(res?.data);
        }).catch((e) => {
            ifError(e);
        });
    
    if(!data){ return <div> Loading ... </div> }
    else{ return data; }
}

export function FetchWithId(dataName, n){
    const [data, setData] = useState({ data : {} });
    const id = getUrlId(n);
    useEffect( () => {
        axios.get(`/${dataName}/${id}`).then((res) => {
                setData(res?.data);
            }).catch((e) => {
                ifError(e);
        });
    }, [dataName, id]);
    if(!data){ return <div> Loading ... </div>}
    else{ return data.data;}
}

export function Delete(dataName, dataId, articleId){
    if(_.isEqual(dataName, "comment")){
        axios.delete(`/${dataName}/${dataId}`).then(() => {
            alert("Successfully deleted.");
            window.location.reload();
        }).catch((e) => {
            alert("Failed to delete.");
            window.location.reload();
        })
    }else{
        axios.delete(`/${dataName}/${dataId}`).then(() => {
            alert("Successfully deleted.");
            window.location.replace(`/${dataName}`);
        }).catch((e) => {
            alert("Failed to delete.");
            window.location.reload();
        });
    }
}
