import { useState } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";

function ArticleList({user, articleList}){
    const [allChecked, setAllChecked] = useState(false);
    const [checkedItems, setCheckedItems] = useState(new Set());

    const checkedItemHandler = (id, isChecked) => {
        if(isChecked) {
          checkedItems.add(id);
          setCheckedItems(checkedItems);
        } else if (!isChecked && checkedItems.has(id)) {
          checkedItems.delete(id);
          setCheckedItems(checkedItems);
        }
    };

    const changeAll = (() => {
        setAllChecked(!allChecked);
        checkedItems.clear();
        if(!allChecked){
            articleList.map((article, index) => {
                checkedItems.add(article.id);
                return setCheckedItems(checkedItems);
            });
        }
        console.log(checkedItems);
    })
    return(
        <>
        {isAdmin(user.auth) && 
            <button className="btn-remove" onClick={() => Array.from(checkedItems).map((id, index) => {
                                                    return Delete("board", id);
                                                })}>  Delete </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                {isAdmin(user.auth) && 
                    <th> <input type={'checkbox'} style={{width: "20px", height: "20px"}}
                            onChange={() => changeAll()}/></th>
                }
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <>
                        <tr id="clickable" key={index}>
                            {isAdmin(user.auth) && <CheckBox id={article.id} allChecked={allChecked} checkedItemHandler={checkedItemHandler} checkedItems={checkedItems}/>}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    </>
                })}
            </tbody>
        </table>
        </>
    )
}

function CheckBox({id, allChecked, checkedItemHandler, checkedItems}){
    const [bChecked, setChecked] = useState(false);
    
    const checkHandler = ({ target }, id) => {
        checkedItemHandler(id, target.checked);
        setChecked(!bChecked);
        if(checkedItems.size === 0){
            setChecked(false);
        }
        // console.log(Array.from(checkedItems));
    };

    return(
        <input  type={'checkbox'} onChange={(e) => {checkHandler(e, id);}}
                checked={bChecked || allChecked ? true : false}
                style={{width: "20px", height: "20px", margin: "11px"}}/>
    )
}


export default ArticleList;
