import React, { Component } from "react";

class ArticleLists extends Component{
    constructor(props){
        this.state = {
            articleList: []
        }
    }

    componentDidMount() {
        this.callApi()
      }
    
      callApi = async () => {
        const RES = await (await fetch('http://localhost:8080/board')).json();
        this.setState({articleList: RES});
      }
}

export default ArticleLists;
