import { useState } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";


function ArticleList({user, articleList}){
    const [visible, setVisible] = useState(false);

    let chkObj = document.getElementsByName("deleteArticle");
    console.log(chkObj);

    chkObj.forEach((element, index) => {
        console.log("item "+ index + " : " + chkObj.item(index))
    });


    // $("input[name='deleteArticle']").click(function() {
    //     let chkListArr = $("input[name='']");
    //     for(var i= 0;i<chkListArr.length;i++){
    //         chkListArr[i].checked = this.checked;
    //     }
    // });

    return(
        <>
        {isAdmin(user.auth) && 
            <button id="btn-remove"
                    onClick={() => { setVisible(!visible) }}>  {!visible ? "Select Delete" : "Cancel Delete"} </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                {visible && <th onClick = {() => {
                                    Delete("board", {/* 삭제할 게시글 id 리스트 */});
                                }} id="del"> Del </th>}
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <>
                        <tr id="clickable" key={index}>
                            {visible && <input type={'checkbox'} key={index} name="deleteArticle" style={{width: "20px", height: "20px", margin: "11px"}}/>}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    </>
                })}
            </tbody>
        </table>
        </>
    )
}

export default ArticleList;
