import React, { Component } from "react";

class ArticleLists extends Component{
    constructor(props){
        super(props);
        this.state = {
            articleList: {
                id: '',
                title: '',
                content: '',
                createdId: '',
                createdAt: '',
                category_name: '',
                visitCnt: '',
                comment: {
                    id: '',
                    content: '',
                    createdAt: ''
                }
            }
        }
    }

    componentDidMount() {
        this.callApi()
      }
    
      callApi = async () => {
        const RES = await (await fetch('/board')).json();
        this.setState({articleList: RES});
        this.setState({
            id : this.state.articleList.id,
            title: this.state.articleList.title
        })
        console.log("Board Data" + this.state)
      }


      render(){
        return (
            <div>
                Board Data : <pre key={this.state.articleList}></pre>
            </div>
        )
      }
}

export default ArticleLists;
