import { useEffect, useState } from "react";
import {PaginationWrapper, PageButton, FaAngleDoubleLeft, FaAngleDoubleRight, FaAngleLeft, FaAngleRight, ButtonWrapper} from "react-paginate";

export const sliceArrayByLimit = (totalPage, limit) => {
    const totalPageArray = Array(totalPage)
      .fill()
      .map((_, i) => i);
    return Array(Math.ceil(totalPage / limit))
      .fill()
      .map(() => totalPageArray.splice(0, limit));
  };

function Page({totalPage, page, setPage}) {
    // 총 페이지 갯수에 따라 Pagination 갯수 정하기, limit 단위로 페이지 리스트 넘기기
    const [currentPageArray, setCurrentPageArray] = useState([]);
    const [totalPageArray, setTotalPageArray] = useState([]);
    const limit = 5;
  
    useEffect(() => {
      if (page % limit === 1) {
        setCurrentPageArray(totalPageArray[Math.floor(page / limit)]);
      } else if (page % limit === 0) {
        setCurrentPageArray(totalPageArray[Math.floor(page / limit) - 1]);
      }
    }, [page]);
  
    useEffect(() => {
      const slicedPageArray = sliceArrayByLimit(totalPage, limit);
      setTotalPageArray(slicedPageArray);
      setCurrentPageArray(slicedPageArray[0]);
    }, [totalPage]);
  
    return (
      <PaginationWrapper>
        <FaAngleDoubleLeft onClick={() => setPage(1)} disabled={page === 1} />
        <FaAngleLeft onClick={() => setPage(page - 1)} disabled={page === 1} />
        <ButtonWrapper>
          {currentPageArray?.map((i) => (
            <PageButton
              key={i + 1}
              onClick={() => setPage(i + 1)}
              aria-current={page === i + 1 ? 'page' : null}
            >
              {i + 1}
            </PageButton>
          ))}
        </ButtonWrapper>
        <FaAngleRight
          onClick={() => setPage(page + 1)}
          disabled={page === totalPage}
        />
        <FaAngleDoubleRight
          onClick={() => setPage(totalPage)}
          disabled={page === totalPage}
        />
      </PaginationWrapper>
    );
  };
  
  export default Page;
