import CommentEdit from "./CommentEdit";
import CommentRegister from "./CommentRegister";
import axios from "axios";
import { isLogin, isAdmin, thisUser } from "../func";

function Comment({article, user}){
    return (
        <div>
            {isLogin() && <CommentRegister user={user}/>}
            <div>
                <b>&lt;Comment List&gt; : {article?.comment?.length} ea</b>
                <div>{article?.comment?.map((comment, index)=>{
                    return <CommentData key={index} data={comment} user={user}/>;
                })}</div>
            </div>
        </div>
    ) 
}


function CommentData({index, data, user}){
    const canDelete = isAdmin(user) || thisUser(data?.user_id);

    function deleteComment(articleId, commentId) {
        axios.delete(`/comment/${commentId}`)
        .then((res) => {
            alert("Comment Deleted");
            window.location.href = `/board/${articleId}`;
        }).catch((e) => {
            alert("Failed to delete comment.\nPlease try again.");
            window.location.href = `/board/${articleId}`;
        });
    }

    return(
        <><li  key={index}>
            <b> User ID : </b> <span> {data?.user_id} </span> <br/>
            <b> Content : </b> <span> {data?.content} </span> <br/>
            <b> Created At : </b> <span> {data?.created_at} </span>
            
            {thisUser(user?.nick_name) && 
                <CommentEdit data={data}/>}
            {canDelete &&
                <button id="btn-remove" onClick={() => { deleteComment(data.article_id, data.id) }}>Delete</button>
            }
        </li> <br/></>
    );
}

export default Comment;
