import axios from "axios";
import _ from "lodash";
import { useEffect, useState, useCallback } from "react";
import { Link } from "react-router-dom";
import "../App.css";
import { ifError } from "../func";

function LoginForm(){
    const [username, setUsername] = useState("");
    const [password, setPqssword] = useState("");
    // const [user, setUser] = useState({ data : {} });
    // const [check, setCheck] = useState(false);
    
    const inputUsername = useCallback(e => {
        setUsername(e.target.value);
    }, [])
    const inputPassword = useCallback(e => {
        setPqssword(e.target.value);
    }, [])

    const login = (e) => {
        axios.post(`/userlogin`, {
            data: {
                username: username,
                password: password
            }
        }).then(() => {
            alert("Login Success.");
        }).catch((e) => {
            alert("Failed to Login.\nPlease try again.");
        });
    }

    // .then(() => {
    //     if(!check){ return Error; }
    //     else{ sessionStorage.setItem("islogin", true); }
    // })
    // const isLogin = (e) => {
    //     axios.get("/logininfo")
    //     .then((res) => {
    //         console.log(res?.data);
    //         setUser(res?.data);
    //     })
    //     .then(() => {
    //         if(!_.isEmpty(user)) {
    //             setCheck(true);
    //         }
    //     })
    //     .catch((e) => {
    //         ifError(e);
    //     })
    // }
    return (
    <div id="div-box">
        <br/>
        <form onSubmit={login}>
            <h2 >Please Login</h2> <hr/>
            <input type="text" id="username" name="username" placeholder="ID (Email)" onChange={inputUsername} required autoFocus/><br/>
            <input type="password" id="password" name="password" placeholder="Password" onChange={inputPassword} required/><br/>
            <button id="btn-post" type="submit">Login</button>
            <Link to="/login/signup"><button id="btn-default">Sign up</button></Link>
        </form> 
    </div>
    )
}

export default LoginForm;
