import React, { useEffect, useState } from "react";
import './App.css'

async function Article({articleList}) {
    const article = await articleList.data;
    return (
      <div>
        <b>ID : </b> <span>{article.id}</span> <br/>
        <b>Title : </b> <span>{article.title}</span> <br/>
        <b>Category : </b> <span>{article}</span> <br/>
        <b>Created By : </b> <span>{article}</span> <br/>
        <b>Created At : </b> <span>{article}</span> <br/>
        <b>Visit : </b> <span>{article}</span> <br/>
        <b>Comment : </b> <span>{article}</span> <br/>
      </div>
    );
}


function ArticleLists(props){
    const [articleList, setArticleList] = useState({
        data : [{
            id: '',
            title: '',
            created_id: '',
            created_at: '',
            category_name: '',
            visit_cnt: '',
            comment_cnt: ''
        }]
    });
    
    useEffect(()=> {
        let resData = null;
        async function fetchData() {
            resData = await props.ArticleLists;
        }
        fetchData();
        setArticleList(resData);
    },[props.ArticleLists, articleList])
    
    // const articles = props.data?.map(article => {
    //     let {Json} = articles.article;
    //     // console.log({Json});
    //     return <div>
    //                 <span>{Json.article}</span>
    //                 <br/>
    //            </div>
    // });
    // console.log(props);
    // console.log(props.ArticleLists);
    
    
    // let {Json} = props.state;

    return (
        <div>
            Board Data(Text â†’ JSON) :
            <Article list={articleList}/>
            {/* <div id="article">
                { props.data?.map(article => (
                    <Article list={article} /> 
                ))}
            </div> */}
        </div>
    )
}

export default ArticleLists;
