import { Link } from "react-router-dom";
import {useState} from "react";

function Bar() {
    const [visible, setVisible] = useState(false);
    return (
        <>
            <div style={{height: "80px", textAlign: "center", backgroundColor: "#aa9dff", position: "relative"}}>
                <b style={{color: "#373737", fontSize: "45px", verticalAlign: "middle"}}> Board </b> 
                <form action="/userlogin?logout" method="post">
                    <button style={{width: "100px", height: "50px" , position: "absolute", right: "10px", top: "-20px"}} type="submit" id="btn-default">Logout</button>
                </form>
                <Link id="none" to="/mypage"><button style={{width: "100px", height: "50px" ,position: "absolute", right: "120px"}} id="btn-default">My Page</button></Link>
            </div>
            {/* 아래 Logout 버튼은 로그인 되어있으면 Logout, 안되어있으면 Login 으로 띄우도록 수정하기 / Logout 일 경우 로그아웃 호출 */}
        </>
    );
}

{/* 
    const [visible, setVisible] = useState(false);
    <>
      <button id="btn-post" 
        onClick={() => { setVisible(!visible); }}> 
        {visible ? "Cancel" : "Edit" } 
      </button>
      {visible && <CommentEditForm data={data} />} 
