import React, { useCallback, useState, useEffect } from "react";
import _ from "lodash";
import { Delete, FetchWithoutId, ifError } from "../func";
import axios from "axios";
import { Link } from "react-router-dom";

function UserManage(){
    //FetchWithoutId 사용 불가 (manage 가 리스트 형태라 useState({data : {}}) 형태로 불러와지지 않음.)
    // data 형태로 보내면 사용할 수 있을 듯
    const [manage, setManage] = useState();
    useEffect(() => {
        axios.get(`/user/manage`)
        .then((res) => {
            setManage(res?.data);
        })
        .catch((e) => {
            ifError(e);
        });
    }, []);
    // const manage = FetchWithoutId("user/manage").data;
    console.log(manage);

    if(!manage){ return <div> Loading ... </div>}
    else{
        return (
            <div id="div-box"> 
                <h1> User List </h1> <hr/>
                {manage.map((userinfo, index) => {
                    return (
                    <li key={index}>
                        <b> ID : </b> <span> {userinfo.email} </span>
                        <b> Name : </b> <span> {userinfo.name} </span> 
                        <b> NickName : </b> <span> {userinfo.nick_name} </span>
                        <b> PhoneNumber : </b> <span> {userinfo.phone} </span>
                        <EditUser userinfo={userinfo} />
                    </li>
                    )
                })}
                <Link id="none" to={`/mypage`}><button id="btn-default">Go to MyPage →</button></Link>
            </div>
        )
    }
}

function EditUser({userinfo}){
    const [userAuth, setAuth] = useState(userinfo.auth);
    
    const setUser = useCallback( e => {
        setAuth(e.target.value);
    }, [])

    const setAdmin = useCallback( e => {
        setAuth(e.target.value);
    }, [])

    const editAuth = (e) => {
        axios.put(`/user/manage/${userinfo.code}`, {
            data : {
                auth : userAuth
            }
        }).then(() => {
            alert("User's auth edited.");
            window.location.reload();
        }).catch((e) => {
            alert("Failed to edit auth.\nPlease try again.");
            window.location.replace("/manage");
            console.log(e.response);
        })
    }

    function authCheck({role}) {
        if(_.isEqual(userinfo.auth, role)){ return true; }
        else { return false; }
    }

    return (
        <div id="div-box" style={{position:"relative"}}>
            <form onSubmit={editAuth}>
                <b> ROLE : </b>
                <input id="radioInput" type="radio" name="auth" value="ROLE_ADMIN" 
                    defaultChecked={authCheck({role:"ROLE_ADMIN"})}  onChange={setAdmin}/> ADMIN 
                <input id="radioInput" type="radio" name="auth" value="ROLE_USER"  
                    defaultChecked={authCheck({role:"ROLE_USER"})}  onChange={setUser}/> USER <br/>
                <button type="submit" id="btn-remove" style={{position:"absolute", right:"130px", top:"-3px"}}>Save</button>
            </form>
            <button id="btn-remove" style={{position:"absolute", right:"50px", top:"-3px"}}
                    onClick={() => Delete("user/manage",userinfo.code)}> Remove </button>
        </div>
    )
}

export default UserManage;
