import React, {Component} from "react";
import ArticleLists from "./articleList";
import Home from "./Home";
import './App.css';
import { Route} from "react-router-dom";

class App extends Component{
    constructor(props){
        super(props);
        this.state = {
                data:  {}
            }
    }

    componentDidMount() {
        this.callBoard();
    }


    callBoard = async () => {
        const RES = await fetch('/board')
                        .then((res) => res.json())
        return this.setState(this.state=RES);
    }

    render(){
        return (
            <div>
                <Route path="/" exact element={Home} />
                <ul id="ulNone"><Route path="/board" element={<ArticleLists ArticleLists={this.state} />} /></ul>
            </div>
        )
        // return( 
        //     <div className="App">
        //         Every Article List : 
        //         <ul id="ulNone"><ArticleLists ArticleLists={this.state} /></ul>
        //     </div>
        // );
    }
}

export default App;
