import { useState, useEffect } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";

function ArticleList({user, articleList}){
    const [checkedItems, setCheckedItems] = useState(new Set());

    const checkedItemHandler = (id, isChecked) => {
        if(isChecked) {
          checkedItems.add(id);
          setCheckedItems(checkedItems);
        } else if (!isChecked && checkedItems.has(id)) {
          checkedItems.delete(id);
          setCheckedItems(checkedItems);
        }
    };

    const checkHandler = ({ target }, id) => {
        checkedItemHandler(id, target.checked);
        console.log(checkedItems);
    };

    const checkAll = (({target}) => {
        articleList.map((article, index) => {
            checkedItemHandler(article.id, target.checked);
            console.log(checkedItems);
        })
    })

    // const [isAllChecked, setIsAllChecked] = useState(false);

    // const allCheckedHandler = (isChecked) => {
    //     if (isChecked) {
    //         setCheckedItems(new Set(articleList?.map((article) => article.id)));
    //         setIsAllChecked(true);
    //     } else {
    //         checkedItems?.clear();
    //         setCheckedItems(setCheckedItems);
    //         setIsAllChecked(false);
    //     }
    // };

    // const allCheckHandler = () => setChecked(isAllChecked);

    return(
        <>
        {isAdmin(user.auth) && 
            <button id="btn-remove"
                    onClick={() => { 
                        }}>  Delete </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                <th> <input type={'checkbox'} style={{width: "20px", height: "20px"}}
                            onChange={(e) => checkAll(e)}/></th>
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <>
                        <tr id="clickable" key={index}>
                            {isAdmin(user.auth) && 
                                <input type={'checkbox'} onChange={(e) => checkHandler(e, article.id)}
                                       key={index} style={{width: "20px", height: "20px", margin: "11px"}}/>}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    </>
                })}
            </tbody>
        </table>
        </>
    )
}

export default ArticleList;
