import axios from 'axios';
import { useCookies } from 'react-cookie';
import {Link} from 'react-router-dom';
import { IsLogin } from './func';

function Home({cookie}) {
    const [,,removeCookie] = useCookies(['login']);
    function Logout(removeCookie) {			
        removeCookie('login');    
        axios.post('/logout');		
    };
    return(
        <div id="home">
            <h1> Home Page </h1>
            <p> App_Board Front Tutorial </p>

            <Link id='none' to="/board"><button id="btn-default"> Board </button></Link>

            <Link id="none" to={`/login`}>
                <button id="btn-default" style={IsLogin(cookie) ? {display : "none"} : {right: "10px"}}> Login </button>
            </Link>
            <form action="/userlogin?logout" method="post">
                <button style={IsLogin(cookie) ? {right: "10px"} : {display : "none"}} type="submit" id="btn-default"
                        onClick={() => Logout()} > Logout </button>
            </form>

            <br/><br/><br/>
        </div>
    );
};

export default Home;
