import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId } from '../func';
import _ from 'lodash';
import { useCallback, useState } from 'react';
import axios from 'axios';

function ArticleLists({user, isLogin}){
    const articleList = Array.from(FetchWithoutId("board").data);

    
    if(_.isEmpty(articleList)){ return <div> Loading... </div> }
    else {
        return (
            <div className='div-box'> 
                <Search />
                <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
                {isLogin && 
                    <Link to={`/board/add/0`} className="none"> 
                        <button className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> 
                                Write article </button></Link>}
                <ArticleList user={user} articleList={articleList}/>
            </div>
        )
    }
}

function Search() {
    const [visible, setVisible] = useState(false);

    return (
        <>
            <button className="w3-button w3-border w3-round-xxlarge w3-small w3-hover-light-gray"
                    onClick={() => {setVisible(!visible)}}>Search</button><br/>
            {visible && <SearchForm />}
        </>
    )
}

function SearchForm(){
    const [searchParam, setSearchParam] = useState("");

    const handleParam = useCallback(e => {
        setSearchParam(e.target.value);
    }, []);
    
    const searchArticle = (e) => {
        e.preventDefault();
        if(!_.isEmpty(searchParam)){
            setSearchParam(searchParam);
        }
        axios.get(`/board/search/${searchParam}`).then((res) => {
            console.log(res.data);
        }).catch((e) => {
            alert("Wrong Access root.")
            window.location.reload();
        })
    }
    console.log(searchParam);
    return (
        <form onSubmit={searchArticle}>
            <input placeholder='Please enter your keyword(s) to search.'
                   onChange={handleParam}/>
            <button type='submit' className="w3-button w3-border w3-round-xxlarge w3-small w3-hover-light-gray">S</button>
        </form>
    )
}

export default ArticleLists;
