import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";


function ArticleList({user, articleList}){
    return(<>
        {isAdmin(user?.auth) && 
            // <input type={'checkbox'} style={{width: '20px', height: '20px', marginTop: "13px"}}
            //        // 선택한 id값들 push 해서 저장해두기 
            // />
            articleList.map((article, index) => {
                return (
                <button id="btn-remove" onClick = {() => {
                                window.event.cancelBubble = true;
                                Delete("board", article.id);
                            }}/>)
            })
        }

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                <th id="item"> ID </th>
                <th id="item"> Title </th>
                <th id="item"> Category </th>
                <th id="item"> Created By </th>
                <th id="item"> Created At </th>
                <th id="item"> Visit </th>
                <th id="item"> Comment </th>
                {/* {isAdmin(user?.auth) && <th id='item'> Del </th>} */}
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return <>
                    <tr id="clickable" key={index} onClick={() => {window.location.href=`/board/${article.id}`}}>
                                <Article data={article} auth={user?.auth}/>
                    </tr>
                    
                    </>
                })}
            </tbody>
        </table>
    </>)
}

export default ArticleList;
