import React, {useState, useEffect} from "react";
import {Link} from 'react-router-dom';

function ArticleDeatil(){
    const urlList = ((window.location.href).split('/'));
    const articleId = urlList[(urlList.length)-1]

    const [articleDetail, setArticleDetail] = useState({
        data : {}
    });

    useEffect(()=> {
        setArticleDetail(articleDetail.data = getArticleDetail(articleId));
    }, [getArticleDetail(articleId), articleDetail]);
    
    return (
        <div>
            <ArticleDetailData key={articleId} data={articleDetail.data[0]} />
        </div>
    )
}

function getArticleDetail(articleId){
    const RES = fetch(`/board/${articleId}`)
            .then(res =>  res.json());
    return RES;
}


function ArticleDetailData({key, data}) {
    
    // const commentDataArr = Array.from(data);

    return (
        <li id="liNone" key={key}> <h1>Article Detail</h1> <br/>
            <b> ID : </b> <span> {data.id} </span> <br/>
            <b> Title : </b> <span> {data.title} </span> <br/>
            <b> Category : </b> <span> {data.category_name} </span> <br/>
            <b> Content : </b> <span> {data.content} </span> <br/>
            <b> Created By : </b> <span> {data.created_id} </span> <br/>
            <b> Created At : </b> <span> {data.created_at} </span> <br/>
            <b> Visit : </b> <span> {data.visit_cnt} </span> <br/>
            <ul id="ulNone"><b> Comment : </b> 
                <b> Comment ID : </b> <span> {data.comment.id} </span>
                <b> Content : </b> <span> {data.comment.content} </span>
                <b> Created At : </b> <span> {data.comment.created_at} </span>
                {/* {commentDataArr.map((comment, index)=>{
                    <CommentData comment={comment} index={index} />
                })} */}
            </ul>
            <nav>
                <Link to={`/`}>
                    <button id="btn-detail"> Home </button></Link>
                <Link to={`/board`}>
                    <button id="btn-detail"> List </button></Link>
            </nav>
        </li>
    );
}

function CommentData({comment, index}){
    return(
        <li id="liNone" key={index}>
            <span> 
                <b> Comment ID : </b> <span> {comment.id} </span>
                <b> Content : </b> <span> {comment.content} </span>
                <b> Created At : </b> <span> {comment.created_at} </span>
            </span> 
            <br/>
        </li>
    )
}


export default ArticleDeatil;
