import React, { useState, Component } from "react";

class ArticleLists extends Component{
    constructor(props){
        super(props);
        this.state = {
            articleList:[
                id = 0,
                title = this.props.title,
                created_id = '',
                created_at = '',
                category_name = '',
                visit_cnt = '',
                comment_cnt = ''

            ]
        }
        // this.state = {
        //         id: '',
        //         title: '',
        //         // content: '',
        //         created_id: '',
        //         created_at: '',
        //         category_name: '',
        //         visit_cnt: '',
        //         comment_cnt: ''
        //         // comment: {
        //         //     id: '',
        //         //     content: '',
        //         //     created_at: ''
        // };
        
    }
    

    componentDidMount() {
        this.callApi();
    }
    
    callApi = async () => {
    const RES = await fetch('/board')
                            .then((res) => res.text())
                            .then((result)=>{
                                const value = JSON.parse(result)
                                console.log(value)
                            })

    this.setState({articleList: RES});
    console.log("Board Data" + this.props)
    }


    render(){
        return (
            <div>
                {/* Board Data(Text → JSON) <pre key={this.state.articleList}> data </pre> */}
                Board Data(Text → JSON) : 
                <pre key={this.props}
                     text={this.state = [
                        id = this.props.id,
                        title = this.state.articleList.title,
                        category_name = this.state.articleList.category_name,
                        created_id = this.state.articleList.created_id,
                        created_at = this.state.articleList.created_at,
                        visit_cnt = this.state.articleList.visit_cnt,
                        comment_cnt = this.state.articleList.comment_cnt
                    ]}></pre>
            </div>
        )
    }
}

export default ArticleLists;
