import React, { useState } from "react";

function ArticleListFunction() {

    const [articleList, setArticleList] = useState({
            id: '',
            title: '',
            // content: '',
            created_id: '',
            created_at: '',
            category_name: '',
            visit_cnt: '',
            comment_cnt: ''
            // comment: {
            //     id: '',
            //     content: '',
            //     created_at: ''
    });
    
    const callApi = async () => {
        const RES = await fetch('/board')
                            .then((res) => res.text())
                            .then((result)=>{
                                const value = JSON.parse(result)
                                console.log(value)
                            })

        setArticleList({articleList: RES});
        console.log("Board Data" + articleList)
        callApi();
    }


    return (
        <div>
            {/* Board Data(Text → JSON) <pre key={this.state.articleList}> data </pre> */}
            Board Data(Text → JSON) : 
            <pre key={articleList}
                    text={articleList = [
                    id = articleList.id,
                    title = articleList.title,
                    category_name = articleList.category_name,
                    created_id = articleList.created_id,
                    created_at = articleList.created_at,
                    visit_cnt = articleList.visit_cnt,
                    comment_cnt = articleList.comment_cnt
                ]}></pre>
        </div>
    )
}

export default ArticleListFunction;
