import { useState, useCallback } from "react";
import _ from "lodash";
import axios from "axios";
import { autoHypenTel } from "../func";
import { Logout } from "../Bar";

function MyInfoEditForm({user}) {
    const [newNickname, setNewNickname] = useState();
    const [newPassword, setNewPassword] = useState();
    const [checkPassword, setCheckPassword] = useState();
    const [newPhone, setNewPhone] = useState();
    
    const inputNickname = useCallback(e => {
        if(!_.isEmpty(e.target.value)){
            setNewNickname(e.target.value);
        }
    }, [])
    
    const inputPassword = useCallback(e => {
        if(!_.isEmpty(e.target.value)){
            setNewPassword(e.target.value);
            document.getElementById("checkPassword").required = true;
        }
    }, [])
    
    const checkNewPassword = useCallback(e => {
        if(!_.isEmpty(newPassword)){
            setCheckPassword(e.target.value);
        }
    }, [newPassword])
    
    const inputPhone = useCallback(e => {
        if(!_.isEmpty(e.target.value)){
            setNewPhone(autoHypenTel(e.target.value));
        }
    }, [])
    
    document.getElementsByTagName("input").forEach(element => {
        console.log(element.value);
    });

    const changeInfo = (e) => {
        e.preventDefault();
        if(!_.isEmpty(checkPassword) && !_.isEqual(newPassword, checkPassword)){
            alert("비밀번호가 서로 맞지 않습니다.\n다시 확인해주세요.");
            return Error;
        }
        
        console.log(checkPassword);
        console.log(newNickname);
        console.log(newPhone);
        
        axios.put(`/user`, {
            email : user.email,
            name : user.name,
            auth : user.auth,
            password : checkPassword,
            nickName : newNickname,
            phone : newPhone
        }).then((res) => {
            alert("사용자 정보가 수정되었습니다.\n다시 로그인하세요.");
            Logout();
            window.location.replace(`/login`);
        }).catch((e) => {
            alert("정보 수정에 실패했습니다.\n" + e.response.statusText);
            window.location.reload(); 
        })
    }

    if(_.isEmpty(user)){ return <div style={{marginTop: "100px", textAlign: "center"}}> <b style={{fontSize: "30px"}}>UserData Not Found</b> </div>}
    else{
        return (
            <form onSubmit={changeInfo} className="div-box" style={{marginTop: "0", height:"20%"}}>
                <b> Edit My Information </b><br/>
                <div className="input-group">
                    <input type="text" name="newName" className="input" onChange={inputNickname} autoFocus/>
                    <label htmlFor="newName" className="input-label">New Nickname</label>
                </div>
                <div className="input-group">
                    <input type="password" name="newPassword" className="input" onChange={inputPassword} />
                    <label htmlFor="newPassword" className="input-label">New Password</label>
                </div>
                <div className="input-group">
                    <input type="password" name="checkPassword" className="input" onChange={checkNewPassword} id="checkPassword"/>
                    <label htmlFor="checkPassword" className="input-label">Check Password</label>
                </div>
                <div className="input-group">
                    <input type="tel" name="newPhone" className="input" onChange={inputPhone} value={newPhone}
                           maxLength="13" pattern="[0-9]{2,3}-[0-9]{3,4}-[0-9]{4}"/>
                    <label htmlFor="newPhone" className="input-label">New PhoneNumber</label>
                </div>
                <button type="submit" className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> Change </button>
            </form>
        )
    }
}

export default MyInfoEditForm;
