import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId } from '../func';

function ArticleLists({user, isLogin}){
    const articleList = Array.from(FetchWithoutId("board").data);
    if(!articleList){ return <div> Loading... </div> }
    else {
        return (
            <> <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
            {isLogin && <Link to={`/board/add/0`} id="none"> <button id="btn-post"> Write article </button></Link>}
            <div className="box1">
                {isAdmin(user?.auth) && 
                    // <input type={'checkbox'} style={{width: '20px', height: '20px', marginTop: "13px"}}
                    //        // 선택한 id값들 push 해서 저장해두기 
                    // />

                    <input type={'checkbox'} id="btn-remove" 
                           onClick = {() => {
                                    window.event.cancelBubble = true;
                                    Delete("board", article.id);
                           }}/>
                    }
            </div>
            <div className="box2"><ArticleList user={user} articleList={articleList} /></div>
            <hr/>
            {/* 페이징 처리 추가 */}
            </>
        )
    }
}

export default ArticleLists;
