import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";


function ArticleList({user, articleList}){
    const [visible, setVisible] = useState(false);
    return(
        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                {isAdmin(user.auth) && 
                    <button id="btn-remove"
                            onClick={() => { setVisible(!visible) }}>  {!visible ? "Select Delete" : "Cancel Delete"} </button>}
                <th id="item"> ID </th>
                <th id="item"> Title </th>
                <th id="item"> Category </th>
                <th id="item"> Created By </th>
                <th id="item"> Created At </th>
                <th id="item"> Visit </th>
                <th id="item"> Comment </th>
                {/* {isAdmin(user?.auth) && <th id='item'> Del </th>} */}
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <tr id="clickable" key={index} onClick={() => {window.location.href=`/board/${article.id}`}}>
                                {visible && <div className='box1' style={{top: "167px"}}><SelectDelete user={user} articleList={articleList}/></div>}
                                <Article data={article} auth={user?.auth}/>
                            </tr>
                })}
            </tbody>
        </table>
    )
}


function SelectDelete({user, articleList}) {
    let checked = document.getElementsByName("deleteArticle");
    console.log(checked);
    return (
        <>
            {isAdmin(user?.auth) && 
                (articleList?.map((article, index) => {
                    return (
                    <>
                    <input type={'checkbox'} key={index} name={"deleteArticle"} style={{width: "20px", height: "20px", margin: "11px"}}/><br/>
                    </>)
                }))
            }
        </>
    )
}

export default ArticleList;
