import React, {Component} from "react";
import ArticleLists from "./articleList";
// import Router from './Router';
import './App.css';
import {  BrowserRouter as Router, Routes, Route} from "react-router-dom";

class App extends Component{
    constructor(props){
        super(props);
        this.state = {
                data:  {}
            }
    }

    componentDidMount() {
        this.callBoard();
    }


    callBoard = async () => {
        const RES = await fetch('/board')
                        .then((res) => res.json())
        return this.setState(this.state=RES);
    }

    render(){
        return( 
            <Router>
                <div>
                    <Routes>
                        <Route path="/board"><ul id="ulNone"><ArticleLists ArticleLists={this.state} /></ul></Route>
                        {/* <Route path="/board/${articleId}"><Aritlce /></Route> */}
                    </Routes>
                </div>
            </Router>
            // <div className="App">
