import React, { useEffect, useState } from "react";
import {Link} from 'react-router-dom';

function CategoryDeatil () {
    const [categoryList, setCategoryList] = useState({ data : {} });
    const [loading, setLoading] = useState(true);

    useEffect( () => {
        const RES = fetch('/category')
                    .then((res) => res.json())
                    .then((result) => {
                        setCategoryList(result);
                        setLoading(false);
                    });
    }, [loading]);

    if(loading) { return <div> Loading ... </div>}
    else {
        return (
            <>
                <table style={{textAlign: "center"}}>
                    <thead>
                        <tr>
                            <th id="item"> ID </th>
                            <th id="item"> NAME </th>
                        </tr>
                    </thead>
                    <tbody>
                        {categoryList?.data?.map((category, index) => {
                            return <tr key={index}><CategoryDeatilData data={category} /></tr>
                        })}
                    </tbody>
                </table>
                <div style={{width: "100%", textAlign: "center", marginTop:"10px"}}>
                    <Link to={`/`} id="none"> <button id="btn-default"> Home </button></Link>
                    <Link to={`/category/add`} id="none"> <button id="btn-default"> Add Category </button></Link>
                    
                </div>
            </>
        )
    }
}

function CategoryDeatilData({data}) {

    function deleteCategory(categoryId, categoryName) {
        const axios = require('axios');

        if(categoryId==="0"){
            alert("You cannot Remove DEFAULT category!!");
            return Error;
        }else{
            alert("Deleted Category " + categoryName + ", the articles are moved to DEFAULT category.");
            axios.delete(`/category/${categoryId}`);
            window.location.href="/category/0";
        }
    }

    return (
        <>
            <td id="item"> <Link to={`/board/category/${data.id}`} id="none"> {data.id} </Link> </td>
            <td id="item"> <Link to={`/board/category/${data.id}`} id="none"> {data.name} </Link> </td>

            <Link to={`/category/${data.id}/edit`} id="none"> <button id="btn-post"> Edit</button></Link>
            <button id="btn-remove" onClick={() => { deleteCategory(data.id, data.name) }}> 
                        Delete </button>
        </>
    )
}

export default CategoryDeatil
