import React, {useState, useEffect} from "react";
import { BrowserRouter as Router, Route, Routes } from "react-router-dom";
import Home from "./Home";
import ArticleLists from "./Article/ArticleLists";
import ArticleDetail from "./Article/ArticleDetail";
import ArticleEdit from "./Article/ArticleEdit";
import ArticleRegister from "./Article/ArticleRegister";
import CategoryRegister from "./Category/CategoryRegister";
import CategoryEdit from "./Category/CategoryEdit";
import ArticlesByCategory from "./Category/ArticlesByCategory";
import CategoryDeatil from "./Category/CategoryDetail";
import './App.css';

function App() {
    // const urlList = ((window.location.href).split('/'));
    // const categoryId = urlList[(urlList.length)-1]
    const [categoryList, setCategoryList] = useState({  data : {}  });
    const [categoryNameList, setCategoryName] = useState();

    const [loading, setLoading] = useState(true);
    
    useEffect(() => {
        const res = fetch('/category')
                    .then((res) => res.json())
                    .then((result) => setCategoryList(result));
        console.log(categoryList.data);
        
        // 카테고리 이름으로만 리스트 만들어서 보내기
        // const matchCategoryId = Number(categoryId);
        // for(let i=0;i<categoryList.data.length;i++){
        //     if(categoryList.data[i].id === matchCategoryId){
        //         setCategoryName(categoryList.data[i].name);
        //         setLoading(false);
        //     }
        // }
    }, [loading])


    if(loading) { return <h1> Loading ... </h1> }
    else {
        return(
            <div><Router><Routes>
                    <Route path="/" exact element={<Home/>} />
                    <Route path="/board" element={<ArticleLists />} />
                    <Route path="/board/:articleId" element={<ArticleDetail />} />
                    <Route path="/board/category/:categoryId" element={<ArticlesByCategory categoryName={categoryList.data}/>} />
                    <Route path="/board/add/:categoryId" element={<ArticleRegister />} />
                    <Route path="/board/:articleId/edit" element={<ArticleEdit />} />
    
                    <Route path="/category" element={<CategoryDeatil />} />
                    <Route path="/category/add/" element={<CategoryRegister />} />
                    <Route path="/category/:categoryId/edit" element={<CategoryEdit />} />
            </Routes></Router></div>
        )
    }
}

