import axios from "axios";
import _ from "lodash";
import { useCallback, useState } from "react";
import { Link } from "react-router-dom";
import "../App.css";

function LoginForm(){
    const [username, setUsername] = useState("");
    const [password, setPassword] = useState("");

    const inputUsername = useCallback(e => {
        setUsername(e.target.value);
    }, []);

    const inputPassword = useCallback(e => {
        setPassword(e.target.value);
    }, []);

    function userLogin(e) {
        e.preventDefault();
        if(_.isEmpty(username)){
            alert("You must input your username");
            return Error;
        }else{setUsername(username);}

        if(_.isEmpty(password)){
            alert("You must input your password");
            return Error;
        }else{setPassword(password);}


        axios.post('/userlogin', {
            username: username,
            password: password
        }).then((res) => {
            alert("Login Successed.\nMove to board.");
            window.location.href="/board";
        }).catch((e) => {
            alert("Error. Please try again.\n" + e.response);
            window.location.replace("/login");
        });
        
    }

    return (
        <div id="div-box">
        <br/>
        <form onSubmit={userLogin}>
            <h2 >Please Login</h2> <hr/>
            <input type="text" id="username" name="username" placeholder="ID (Email)" required autoFocus/><br/>
            <input type="password" id="password" name="password" placeholder="Password" required/><br/>
            <button id="btn-post" type="submit">Login</button>
            <Link to="/login/signup"><button id="btn-default" type="submit">Sign up</button></Link>
        </form> 
        <Link id="none" to="/"><button id="btn-default">Home</button></Link>
    </div>
    )
}

export default LoginForm;
