import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import CategoryBar from "../Category/CategoryBar";
import Bar from "../Bar";
import '../App.css'
import {useState, useEffect} from 'react';
import axios from 'axios';
import { FetchWithoutId, isLogin } from '../func';

function ArticleLists({user}){
    const articleList = FetchWithoutId("board");
    console.log(articleList);
    // const [articleList, setArticleList] = useState({
    //     data : {}
    // });

    // useEffect(()=> {
    //     axios.get('/board')
    //         .then((res) => {
    //             setArticleList(res?.data);
    //         });
    // }, [])

    const articleListArr = Array.from(articleList?.data);
    if(!articleListArr){ return <div> Loading... </div> }
    else {
        return (
            <>  {console.log()}
                <Bar /> 
                {isLogin() && <CategoryBar user={user}/>}
                <ArticleList user={user} articleList={articleListArr} /><br/>

                <div style={{width: "100%", textAlign: "center"}}>
                    <Link to={`/`} id="none"> <button id="btn-default"> Home </button></Link>
                    {isLogin() && <Link to={`/board/add/0`} id="none"> <button id="btn-post"> Write </button></Link>}
                </div>
            </>
        )
    }
}

export default ArticleLists;
