import { useState, useEffect } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";
import CheckBox from "./CheckBox";

function ArticleList({user, articleList}){
    const [allChecked, setAllChecked] = useState(false);
    const [bChecked, setChecked] = useState(false);
    const [checkedItems, setCheckedItems] = useState(new Set());
    
    const checkAll = (() => {
        setAllChecked(true);
    })
    return(
        <>
        {isAdmin(user.auth) && 
            <button id="btn-remove"
                    onClick={() => { 
                        }}>  Delete </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                <th> <input type={'checkbox'} style={{width: "20px", height: "20px"}}
                            onChange={() => checkAll()}/></th>
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <>
                        <tr id="clickable" key={index}>
                            {isAdmin(user.auth) && <CheckBox key={index} id={article.id} checkedItems={[checkedItems, setCheckedItems]}/>}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    </>
                })}
            </tbody>
        </table>
        </>
    )
}

export default ArticleList;
