import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId } from '../func';
import { useState } from 'react';
import _ from 'lodash';
import Pagination from 'react-js-pagination';

function ArticleLists({user, isLogin}){
    const limit = 5;
    const [currentPage, setCurrentPage] = useState(1);
    const articleList = Array.from(FetchWithoutId("board").data);

    
    if(_.isEmpty(articleList)){ return <div> Loading... </div> }
    else {
        return (
            <> 
                <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
                {isLogin && 
                    <Link to={`/board/add/0`} className="none"> 
                        <button className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> 
                                Write article </button></Link>}
                <ArticleList user={user} articleList={articleList} limit={limit} currentPage={currentPage}/> <hr/>
                
                <Paging articleList={articleList} limit={limit} page={currentPage} setPage={setCurrentPage} />
            </>
        )
    }
}

function Paging({articleList, limit, page, setPage}) {
    const articlesPerPage = 10;
    const handlePageChange = (page) => {
      setPage(page);
    };
  
    return (
        <Pagination style={{textAlign: "center"}}
            activePage={page} 
            itemsCountPerPage={articlesPerPage} 
            totalItemsCount={articleList.length} 
            pageRangeDisplayed={articlesPerPage} 
            prevPageText={"‹"} 
            nextPageText={"›"} 
            onChange={handlePageChange}
            activeClass={"w3-button w3-round-xxlarge w3-small w3-deep-purple"}
            itemClass={"w3-button w3-round-xxlarge w3-small w3-hover-deep-purple"}
            linkClass={"none"}
            hideDisabled={true}
        />
    );
  };

export default ArticleLists;
