import React, { useCallback, useState, useEffect } from "react";
import { split } from "lodash";
import { ifError } from "../func";
import axios from "axios";

function UserManage(){
    const [visible, setVisible] = useState(false);
    

    const [manage, setManage] = useState();
    useEffect(() => {
        axios.get(`/user/manage`)
        .then((res) => {
            setManage(res?.data.data);
        })
        .catch((e) => {
            ifError(e);
        });
    }, []);

    console.log(manage);
    if(!manage){ return <div> Loading ... </div>}
    else{
        return (
            <div id="div-box"> 
                <h1> User List </h1> <hr/>
                {manage.map((userinfo, index) => {
                    return (
                    <li key={index}>
                        <b> ID : </b> <span> {userinfo.email} </span>
                        <b> Name : </b> <span> {userinfo.name} </span> 
                        <b> NickName : </b> <span> {userinfo.nick_name} </span>
                        <b> PhoneNumber : </b> <span> {userinfo.phone} </span>
                        <AuthEdit data={userinfo} />
                        
                        {/* <b> Auth : </b> <span> {split(userinfo.auth,"_")[1]} </span>
                        <button id="btn-post" onClick={() => {setVisible(!visible)}}>
                            {visible ? "Cancel" : "Edit"} </button>
                        {visible && <AuthEdit data={userinfo}/>} */}
                    </li>
                    )
                })}

            </div>
        )
    }
}

function AuthEdit({userinfo}){
    const [userAuth, setAuth] = useState(userinfo?.auth);
    
    const setUser = useCallback( e => {
        setAuth(e.target.value);
    }, [])

    const setAdmin = useCallback( e => {
        setAuth(e.target.value);
    }, [])

    const editAuth = (e) => {
        axios.put(`/user/manage/${userinfo.code}`, {
            data : {
                email : userinfo.email,
                auth : userAuth
            }
        }).then(() => {
            alert("User's auth edited.");
            window.location.reload();
        }).catch((e) => {
            alert("Failed to edit auth.\nPlease try again.");
            window.location.replace("/manage");
            console.log(e.response);
        })
    }

    return (
        <form onSubmit={editAuth}>
            <b> ROLE : </b> 
            <input type="radio" name="auth" value="ROLE_ADMIN" style={{width: "30px", borderRight: "1px solid black"}} onChange={setAdmin}/> ADMIN 
            <input type="radio" name="auth" value="ROLE_USER"  style={{width: "30px"}} onChange={setUser}/> USER <br/>
        </form>
    )
}

export default UserManage;
