import { useState } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import Pagination from "react-js-pagination";
import "../App.css";


function ArticleList({user, articleList}){
    const [allChecked, setAllChecked] = useState(false);
    const [checkedItems, setCheckedItems] = useState(new Set());
    
    const limit = 5;
    const articlesPerPage = 10;
    const [currentPage, setCurrentPage] = useState(1);
    const offset = (currentPage - 1) * articlesPerPage;

    const checkedItemHandler = (id, isChecked) => {
        if(isChecked) {
            checkedItems.add(id);
            setCheckedItems(checkedItems);
        } else if (!isChecked && checkedItems.has(id)) {
            checkedItems.delete(id);
            setCheckedItems(checkedItems);
        }
    };

    const checkHandler = ({ target }, id) => {
        if(target.checked===false){setAllChecked(false)}
        checkedItemHandler(id, target.checked);
    };

    const changeAll = (() => {
        setAllChecked(!allChecked);
        for(var i = 0;i < articleList.slice(offset, offset + articlesPerPage).length; i++){
            const item = document.getElementsByName("check")[i];
            item.checked = !allChecked;
            checkedItemHandler(Number(item.value), item.checked);
        }
    })

    const handlePageChange = (currentPage) => {
        setPage(currentPage);
        setAllChecked(false);
    };

    
    return(
        <>
        {isAdmin(user.auth) && 
            <button className="w3-button w3-border w3-round-xlarge w3-small w3-hover-red" 
                    onClick={() => Array.from(checkedItems).map((id, index) => {
                                return Delete("board", id);
                            })}>  Delete </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                {isAdmin(user.auth) && 
                    <th> <input type={'checkbox'} style={{width: "20px", height: "20px"}}
                                checked={allChecked} onChange={() => changeAll()}/></th>
                }
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList.slice(offset, offset + articlesPerPage).map((article, index) => {
                   return (
                    <tr id="clickable" key={index}>
                        {isAdmin(user.auth) && 
                            <input  type={'checkbox'} onChange={(e) => {checkHandler(e, article.id);}}
                            name="check" value={article.id}
                            style={{width: "20px", height: "20px", margin: "11px"}}/>
                        }
                        <Article data={article} auth={user?.auth}/>
                    </tr>
                )})}
            </tbody>
        </table> <hr/>
        <Pagination
            activePage={page} 
            itemsCountPerPage={articlesPerPage} 
            totalItemsCount={articleList.length} 
            pageRangeDisplayed={limit} 
            onChange={handlePageChange}
            innerClass={""}
            activeClass={"w3-button w3-round-xxlarge w3-small w3-deep-purple"}
            itemClass={"w3-button w3-round-xxlarge w3-small w3-hover-deep-purple"}
            linkClass={"none"}
            // hideDisabled={true}
        />
        </>
    )
}

function Paging({articleList, limit, articlesPerPage, page, setPage, setAllChecked}) {
    const handlePageChange = (page) => {
      setPage(page);
      setAllChecked(false);
    };
  
    return (
        <Pagination
            activePage={page} 
            itemsCountPerPage={articlesPerPage} 
            totalItemsCount={articleList.length} 
            pageRangeDisplayed={limit} 
            onChange={handlePageChange}
            innerClass={""}
            activeClass={"w3-button w3-round-xxlarge w3-small w3-deep-purple"}
            itemClass={"w3-button w3-round-xxlarge w3-small w3-hover-deep-purple"}
            linkClass={"none"}
            // hideDisabled={true}
        />
    );
};

export default ArticleList;
