import {Link} from 'react-router-dom';
import ArticleList from './ArticleList';
import '../App.css'
import { FetchWithoutId } from '../func';
import { useState } from 'react';
import { ReactPaginate, Items } from 'react-paginate';

function ArticleLists({user, isLogin}){
    const articleList = Array.from(FetchWithoutId("board").data);
    console.log(articleList);
    if(!articleList){ return <div> Loading... </div> }
    else {
        return (
            <> 
                <b style={{ fontSize: "30px", margin : "10px"}}> Article List </b>
                {isLogin && 
                    <Link to={`/board/add/0`} id="none"> 
                        <button className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> 
                                Write article </button></Link>}
                <ArticleList user={user} articleList={articleList} /> <hr/>
                {/* 페이징 처리 추가 */}
                <Paging articleList={articleList}/>
            
            </>
        )
    }
}

function Paging({articleList}){
    // const [posts, setPosts] = useState([]); articleList
    const [currentPage, setCurrentPage] = useState(1);
    const [postsPerPage, setPostPerPage] = useState(10);

    const indexOfLast = currentPage * postsPerPage;
    const indexOfFirst = indexOfLast - postsPerPage;
    const currentPosts = (posts) => {
        let currentPosts = 0;
        currentPosts = posts.slice(indexOfFirst, indexOfLast);
        return currentPosts;
    };

    const changePage = (page) => {
        setCurrentPage(page);
    }

    console.log(articleList);
    return (
        <>
            <Items currentItems={currentPosts(articleList)} />
            <ReactPaginate
                activePage={currentPage}
                itemsCountPerPage={5}
                totalItemsCount={articleList.length}
                pageRangeDisplayed={5}
                prevPageText={"<<"}
                nextPageText={">>"}
                onChange={changePage}
            />
        </>
    );
}

export default ArticleLists;
