import {useState, useCallback, useEffect} from "react";


function CommentEdit(){
    const urlList = ((window.location.href).split('/'));
    const commentId = urlList[(urlList.length)-2];
    
    const [comment, setComment] = useState({
        data : {}
    });
    const [loading, setLoading] = useState(true);
    
    useEffect(()=> {
        const RES = fetch(`/comment/${commentId}`)
        .then(res =>  res.json())
        .then(result => {
            setComment(result);
            setLoading(false);
        });
    },[]);

    if(loading) {return <div> Loading ... </div>}
    else {  return  <CommentEditForm userIdOrigin={comment.data.userId} contentOrigin={comment.data.content} articleId={comment.data.articleId} /> }
}

function CommentEditForm({userIdOrigin, contentOrigin, articleId}){
    const axios = require('axios');
    const [content, setContent] = useState(contentOrigin);
    
    const editcontent = useCallback( e => {
        setContent(e.target.value);
    }, [])

    const isEmpty = function(value){
        if(value === "" || value === null || value === undefined || ( value !== null && typeof value === "object" && !Object.keys(value).length)){
            return true;
        }else { return false; }
    }

    const editComment = (e) => {
        if(isEmpty(e.target.value)){ setContent(contentOrigin); }

        axios.put(`/comment/${commentId}`, {
            data : {
                content : content
            }
        })
        alert("comment edited.");
    }


    return (
        <><br/><br/><br/>
        <form onSubmit={editComment}>
            <div id="div-box">
                <b style={{textAlign: "center"}}> Edit comment </b> <br/>
                <input id="id-box" placeholder={userIdOrigin} readOnly></input> <br/>
                <input id="id-box" placeholder={contentOrigin} onChange={editcontent}></input> <br/>
                <button type="submit" id="btn-post" style={{textAlign: "right"}}
                     onClick={() => {window.location.href=`/board/${articleId}`}}> Save </button>
            </div>
        </form></>
    )
}

export default CommentEdit;
