import { Link } from "react-router-dom";

function Bar() {
    return (
        <>
            <div style={{height: "80px", textAlign: "center", backgroundColor: "#aa9dff"}}>
                <b style={{color: "#373737", fontSize: "45px", verticalAlign: "middle"}}> Board </b> 
            </div>
            {/* 아래 Logout 버튼은 로그인 되어있으면 Logout, 안되어있으면 Login 으로 띄우도록 수정하기 / Logout 일 경우 로그아웃 호출 */}
            <form action="@/logout" method="post">
                <input type="submit" class="btn btn-info" value="Logout" />
                {/* <Link id="none" to={"/"}>
                    <button type="submit" id="btn-default" style={{float: "right", width: "100px", height: "40px"}}> Logout </button>
                </Link> */}
            </form>
            {/* <Link id="none" to="/"> 
                <button id="btn-default" style={{float: "right", width: "100px", height: "40px"}}> Logout </button>
            </Link> */}
        </>
    );
}

export default Bar;
