import { useEffect, useState } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";

function ArticleList({user, articleList}){
    let chkObj = document.getElementsByName("checkOne");
    const [inputList, setInputList] = useState(chkObj);
    const [checkedInputs, setCheckedInputs] = useState([]);

    useEffect(() => {
        setInputList(chkObj);
        console.log(inputList);
        Array.from(inputList).map((checkbox, index) => {
            console.log(index + " : " + checkbox.checked);
        })
    },[chkObj]);

    function checkedHandler(checked, id) {
        if(checked){
            setCheckedInputs([...checkedInputs, id]);
        }else{
            setCheckedInputs(checkedInputs.filter((el) => el !== id));
        }
    }

    function checkedDeleteHandler(checkedIdList) {
        checkedIdList.map((id, index) => {
            Delete("board", id);
        })
    }


    return(
        <>
        {isAdmin(user.auth) && 
            <button id="btn-remove"
                    onClick={() => { 
                        // removeChild(checkId);
                        checkedDeleteHandler(); }}>  Delete </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                <th> <input type={'checkbox'} name={'checkAll'} style={{width: "20px", height: "20px"}}/></th>
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <>
                        <tr id="clickable" key={index}>
                            {isAdmin(user.auth) && 
                                <input type={'checkbox'} onChange={checkedHandler()}
                                       key={index} name={'checkOne'} style={{width: "20px", height: "20px", margin: "11px"}}/>}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    </>
                })}
            </tbody>
        </table>
        </>
    )
}

export default ArticleList;
