import axios from "axios";
import { split } from "lodash";
import { useState } from "react";
import * as Function from "../func";

function MyPage(){
    // const userInfo = Function.Fetching("user", 1);

    const [user, setUser] = useState("");
    const userName = sessionStorage.getItem("username");
    axios.get(`/user/${userName}`).then((res) => {
        setUser(res.data);
        console.log(user);
    });
    // const auth = split(user?.authorities[0]?.authority, "_")[1];
    // console.log(auth);

    if(!user) { return <div> Loading ... </div> }
    else {
        return (
            <div id="div-box">
                <h1>MY PAGE</h1><hr/>
                <div style={{textAlign: "left", border: "1px solid black", margin: "20px"}}>
                    <b> User Code : </b><span> {user.code} </span><br/>
                    <b> User Name : </b><span> {user.username} </span><br/>
                    <b> PassWord : </b><span> {user.password} </span><br/>
                    <b> ROLE : </b><span> {split(user?.authorities[0]?.authority, "_")[1]} </span><br/>
                </div>
            </div>
        )
    }
}

export default MyPage;
