import { useEffect, useState } from "react";
import Article from "./Article";
import { isAdmin, Delete } from "../func";
import "../App.css";

function ArticleList({user, articleList}){
    const [visible, setVisible] = useState(true);
    let [inputList, setInputList] = useState(document.getElementsByName("checkOne"));

    useEffect(() => {
        setInputList(document.getElementsByName("checkOne"));
        console.log(inputList);
        Array.from(inputList)?.map((checkbox, index) => {
            console.log(index + " : " + checkbox.checked);
        })
    },[]);



    function allCheck() {
        let checkList = `$("input[name='checkOne']")`;
        for(var i = 0; i < checkList.length; i++){
            checkList[i].checked = this.checked;
        }
    }

    // 삭제 버튼
    // const checkId = checkedArr.map(item => {
    //     return item.id;
    // });

    // const removeChild = id => {
    //     let removeProducts;
    //     id.forEach(
    //         item => (removeProducts = products.filter(
    //             product => product.produtc_id !== item
    //         ))
    //     );
    //     id.forEach(
    //         item => (removeProducts = removeProducts.filter(
    //             removeProducts => removeProducts.product_id !== item
    //         ))
    //     );
    //     setProduct(removeProducts);
    // }

    function checkedDeleteHandler(checkedIdList) {
        checkedIdList.map((id, index) => {
            Delete("board", id);
        })
    }


    return(
        <>
        {isAdmin(user.auth) && 
            <button id="btn-remove"
                    onClick={() => { 
                        // removeChild(checkId);
                        checkedDeleteHandler(); }}>  Delete </button>}

        <table id="list">
            <thead style={{borderBottom: "2px solid #000000", backgroundColor: "#aa9dff"}}>
            <tr>
                <th> <input type={'checkbox'} name={'checkAll'} style={{width: "20px", height: "20px"}}/></th>
                <th> ID </th>
                <th> Title </th>
                <th> Category </th>
                <th> Created By </th>
                <th> Created At </th>
                <th> Visit </th>
                <th> Comment </th>
            </tr>
            </thead>
            <tbody>
                {articleList?.map((article, index) => {
                    return  <>
                        <tr id="clickable" key={index}>
                            {visible && <input type={'checkbox'} key={index} name={'checkOne'} value={article.id}
                                               style={{width: "20px", height: "20px", margin: "11px"}}/>}
                            <Article data={article} auth={user?.auth}/>
                        </tr>
                    </>
                })}
            </tbody>
        </table>
        </>
    )
}

export default ArticleList;
