import React, { useCallback, useState } from "react";
import _ from "lodash";
import { Delete, FetchWithoutId } from "../func";
import axios from "axios";

function UserManage(){
    const manage = Array.from(FetchWithoutId("user/manage").data);
    
    if(_.isEmpty(manage)){ return <div> Loading ... </div>}
    else{
        return (
            <>
            <div className="div-box">
                <ul>
                    {manage?.map((userinfo, index) => {
                        return (
                            <EditUser key={index} userinfo={userinfo} />
                        )
                    })}
                </ul>
            </div>
            </>
        )
    }
}

function EditUser({userinfo}){
    const authList = ["ROLE_USER", "ROLE_ADMIN"];
    const [userAuth, setAuth] = useState(userinfo.auth.split("_")[1]);
    const [visible, setVisible] = useState(false);

    function changeAuth(){
        if(_.isEqual(userAuth, "USER")){
            setAuth(authList[1].split("_")[1]);
        }else{
            setAuth(authList[0].split("_")[1]);
        }
    }

    const editAuth = (e) => {
        e.preventDefault();
        axios.put(`/user/manage`, {
            data : {
                auth : "ROLE_" + userAuth
            }
        }).then(() => {
            console.log("ROLE_" + userAuth);
            alert("User's auth edited.");
        }).catch((e) => {
            alert("Failed to edit auth.\nPlease try again.");
            console.log(e.response);
        })
    }

    function authCheck({role}) {
        if(_.isEqual(userinfo.auth, role)){ return true; }
        else { return false; }
    }

    return (
        <>
            <li className="manage" onClick={() => setVisible(!visible)}>
                <td>{userinfo.code}</td>
                <td>{userinfo.email}</td>
                <td>{userinfo.name}</td>
                <td>{userinfo.nick_name}</td>
                <td>{userinfo.phone}</td>
                <td>{userAuth} 
                    <input type={"image"} src={require("../change.png").default} alt={"icon"}
                        style={{width:"20px", verticalAlign: "middle"}}
                        onClick={() => {changeAuth()}} /> </td>
            </li>
            {visible && <form onSubmit={editAuth}>
                <b> current auth : {userAuth} &nbsp; </b>
                
                <button type="submit" className="w3-button w3-border w3-round-xlarge w3-small w3-hover-teal"> Save </button>
            </form>}
        </>
    )
}

export default UserManage;
