import React, { Component } from "react";

class ArticleLists extends Component{
    constructor(props){
        super(props);
        this.state = {
            articleList: {
                id: '',
                title: '',
                // content: '',
                created_id: '',
                created_at: '',
                category_name: '',
                visit_cnt: '',
                comment_cnt: ''
                // comment: {
                //     id: '',
                //     content: '',
                //     created_at: ''
                // }
            }
        }
    }

    componentDidMount() {
        this.callApi()
      }
    
      callApi = async () => {
        const RES = await (await fetch('/board')
                                .then((res) => res.text())
                                .then((result)=>{
                                    const value = JSON.parse(result)
                                    // console.log(value)
                                }))

        this.setState({articleList: RES});
        // console.log("Board Data" + this.state.articleList)
      }


      render(){
        return (
            <div>
                Board Data(Text â†’ JSON) <pre key={this.state.articleList}> {this.callApi} </pre>
