import React, {Component} from "react";
import ArticleLists from "./articleList";
import Home from "./Home";
import './App.css';
import { Route, Routes } from "react-router-dom";

class App extends Component{
    constructor(props){
        super(props);
        this.state = {
                data:  {},
                detail: {}
            }
    }

    componentDidMount() {
        this.callBoard();
    }


    callBoard = async () => {
        const RES = await fetch('/board')
                        .then((res) => res.json())
        return this.setState(this.state.data=RES);
    }

    callArticle = async () => {
        articleId = detail.id;
        const RES = await fetch(`/board/${articleId}`)
                        .then((res) => res.json())
        return this.setState(this.state.detail=RES);
    }

    render(){
        return (
            <div><Routes>
                <Route path="/" exact element={<Home/>} />
                <Route path="/board" element={<ArticleLists ArticleLists={this.state.data} />} />
                <Route path="/board/:articleId" element={<ArticleDetail ArticleDetail={this.state.detail}/>} />
            </Routes></div>
        )
        // return( 
        //     <div className="App">
        //         Every Article List : 
        //         <ul id="ulNone"><ArticleLists ArticleLists={this.state} /></ul>
        //     </div>
        // );
    }
}

export default App;
