import React, {Component} from "react";
import ArticleLists from "./articleList";
import './App.css';

class App extends Component{
    constructor(props){
        super(props);
        this.state = {
                data: [
                // {
                //     id: '',
                //     title: '',
                //     created_id: '',
                //     created_at: '',
                //     category_name: '',
                //     visit_cnt: '',
                //     comment_cnt: ''
                // },
            ]
        }
    }

    componentDidMount() {
        this.callApi();
    }

    // responseHandler = ({data}) => {
    //     this.setState(data);
    //     return data;
    // };

    callApi = async () => {
        const RES = await fetch('/board')
                        .then((res) => res.json())
                        // .then((result)=> console.log(result))


        // const RES = await (await fetch('/board')).json();
        // console.log(RES);
        // this.setState(this.state=RES);
        // console.log(this.state);
        return this.setState(this.state=RES);
    }

    render(){
        return(
            <div className="App">
                <p>{this.state}</p>
                <ArticleLists ArticleLists={this.state} />
            </div>
        );
    }
}

export default App;
