import { useState, useCallback } from "react";
import { Link } from "react-router-dom";
import * as Function from "../func";
import axios from "axios";
import "../App.css";


function SignupForm(){
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [auth, setAuth] = useState("");

    const inputEmail = useCallback(e => {
        setEmail(e.target.value);
    }, [])

    const inputPassword = useCallback(e => {
        setPassword(e.target.value);
    }, [])
    
    const inputAuth = useCallback(e => {
        setAuth(e.target.value);
    }, [])


    const signUp = (e) => {
        e.preventDefault();

        if(Function.isEmpty(email)){
            alert("You must input your Email");
            return Error;
        }else{setEmail(email);}

        if(Function.isEmpty(password)){
            alert("You must input password");
            return Error;
        }else{setPassword(password);}

        let userData = {
            data : {
                email : email,
                password : password,
                auth : auth
            }
        }

        axios.post('/user', JSON.stringify(userData), {
            headers: {
                "Content-Type": `application/json`, // application/json 타입 선언
            }
        });
        
        alert("Register Success. \n Please Login.");
    }

    return (
        <div id="div-box">
            <h1>Sign Up</h1> <hr/>

            <form typ onSubmit={signUp}>
                <input type="text" name="email" placeholder="E-Mail" onChange={inputEmail}/> <br/>
                <input type="password" name="password" placeholder="Password" onChange={inputPassword}/> <br/>
                <input type="radio" name="auth" value="ROLE_ADMIN, ROLE_USER" style={{"width": "30px"}} onChange={inputAuth}/> ADMIN <br/>
                <input type="radio" name="auth" value="ROLE_USER" defaultChecked="checked" style={{"width": "30px"}} onChange={inputAuth}/> USER <br/>
                <button type="submit" id="btn-post">Join</button>
            </form>

            <Link id="none" to="/"><button id="btn-default">Go to Login →</button></Link>
        </div>
    )
}

export default SignupForm;
