import React, {useState, useEffect} from "react";
import {Link} from 'react-router-dom';
import Article from './Article';

function ArticlesByCategory(categoryName){
    console.log(categoryName);

    const urlList = ((window.location.href).split('/'));
    const categoryId = urlList[(urlList.length)-1]

    const [articleByCategory, setArticleByCategory] = useState({
        data : {}
    });
    const [loading, setLoading] = useState(true);

    useEffect(()=> {
        const RES = fetch(`/category/board/${categoryId}`)
                    .then(res =>  res.json())
                    .then(result => {
                        setArticleByCategory(result);
                        setLoading(false);
                    });
    },[articleByCategory, categoryId]);

    const articleListArr = articleByCategory.data;

    if(loading) { return <div> Loading ... </div> }
    else {
        return (
        <div>
            <h1 style={{color: "#373737", textAlign:"center"}}> Category : {articleListArr[0]?.category_name} </h1>
            <table id="list">
                <thead>
                    <tr>
                        <th id="item"> ID </th>
                        <th id="item"> Title </th>
                        <th id="item"> Category </th>
                        <th id="item"> Created By </th>
                        <th id="item"> Created At </th>
                        <th id="item"> Visit </th> 
                        <th id="item"> Comment </th>
                    </tr>
                </thead>
                <tbody>
                    {articleListArr.map((article, index) => (
                        <tr key={index}><Article data={article} /></tr>
                        ))}
                </tbody>
            </table>
            <br/>
            <div style={{width: "100%", textAlign: "center"}}>
                <Link to={`/`} id="none"> <button id="btn-default"> Home </button></Link>
                <Link to={`/category`} id="none"><button id="btn-default"> Category List </button></Link>
                <Link to={`/`} id="none"> <button id="btn-default"> Add Article </button></Link>
            </div>
        </div>
    )}
}


export default ArticlesByCategory;
