import { Link } from "react-router-dom";
import {useState} from "react";
import * as Function from "./func";

function Bar() {
    return (
        <>
            <div style={{height: "80px", textAlign: "center", backgroundColor: "#aa9dff", position: "relative"}}>
                <b style={{color: "#373737", fontSize: "45px", verticalAlign: "middle"}}> Board </b> 
                {/* 로그인 되어있으면 MyPage / Logout, 안되어있으면 Login 만 띄우도록 수정
                    현재는 클릭하면 Login/ Logout 이 번갈아 나오는 상태 */}
                <Link id="none" to={`/login`}>
                    <button id="btn-user" style={{right: "10px", display: Function.isLogin() ? "hidden" : "inline-block"}}> Login </button>
                </Link>
                <form action="/userlogin?logout" method="post">
                    <button style={{right: "10px", display: Function.isLogin() ? "hidden" : "inline-block"}} type="submit" id="btn-user"
                            onClick={() => sessionStorage.removeItem('username')} > Logout </button>
                </form>
                <Link id="none" to={`/mypage/${sessionStorage.getItem('username')}`}>
                    <button style={{right: "120px"}} 
                            id="btn-user">My Page</button>
                </Link>
            </div>
        </>
    );
}

export default Bar;
