import { Link } from "react-router-dom";
import { IsLogin } from "./func";
import useCookies from "react-cookie/cjs/useCookies";
import axios from "axios";

function Bar({cookie}) {
    const [,,removeCookie] = useCookies(['login']);
    function Logout() {			
        removeCookie('login');    
        axios.post('/logout');		
    };
    return (
        <>
            <div style={{height: "80px", textAlign: "center", backgroundColor: "#aa9dff", position: "relative"}}>
                <b style={{color: "#373737", fontSize: "45px", verticalAlign: "middle"}}> Board </b> 
                <Link id="none" to={`/login`}>
                    <button id="btn-user" style={IsLogin(cookie) ? {display : "none"} : {right: "10px"}}> Login </button>
                </Link>
                <form action="/userlogin?logout" method="post">
                <button style={IsLogin(cookie) ? {right: "10px"} : {display : "none"}} type="submit" id="btn-default"
                        onClick={() => Logout()} > Logout </button>
                </form>
                <Link id="none" to={`/mypage`}>
                    <button style={IsLogin(cookie) ? {right: "120px"} : {display : "none"}} 
                            id="btn-user">My Page</button>
                </Link>
            </div>
        </>
    );
}

export default Bar;
