import React, { Component } from "react";

class ArticleLists extends Component{
    constructor(props){
        super(props);
        this.state = {
            articleList: {
                id: '',
                title: '',
                content: '',
                createdId: '',
                createdAt: '',
                category_name: '',
                visitCnt: '',
                comment: {
                    id: '',
                    content: '',
                    createdAt: ''
                }
            }
        }
    }

    componentDidMount() {
        this.callApi()
      }
    
      callApi = async () => {
        const RES = await (await fetch('/board')).json();
        this.setState({articleList: RES});
        this.setState({
            id : this.state.articleList.id,
            title: this.state.articleList.title
        })
        console.log("Board Data" + this.state)
      }


      render(){
        return (
            <div>
                Board Data : <pre key={this.state.articleList.id} id={this.state.articleList.id} title={this.state.articleList.title}
                                 content={this.state.articleList.content} createdid={this.state.articleList.createdId} createdat={this.state.articleList.createdAt}
                                 category_name={this.state.articleList.category_name} visitcnt={this.state.articleList.visitCnt}
                                 comment={
                                    [id=this.state.articleList.comment.id,
                                    content=this.state.articleList.comment.content,
                                    created_at=this.state.articleList.comment.created_at]}>Data</pre>
            </div>
        )
      }
}

export default ArticleLists;
